mapper.select:
SELECT
    ne_repository_name
FROM
    rdupm_nexus_repository


        config_id = #{configId}


        AND project_id = #{projectId}


        AND repo_type = #{repoType}

SELECT
    repository_id,
    ne_repository_name,
    project_id,
    organization_id
FROM
    rdupm_nexus_repository


        config_id = #{configId}


        AND organization_id = #{nexusRepository.organizationId}


        AND project_id = #{nexusRepository.projectId}


        AND repo_type = #{nexusRepository.repoType}
