mapper.select:
SELECT
    nr.repository_id,
    nr.ne_repository_name,
    nu.user_id,
    nu.ne_user_id,
    nu.ne_user_password,
    nu.object_version_number
FROM
    rdupm_nexus_repository nr
INNER JOIN rdupm_nexus_user nu ON nu.repository_id = nr.repository_id
WHERE nu.is_default = 1

    AND nr.organization_id = #{organizationId}


    AND nr.repository_id = #{repositoryId}


    AND nr.project_id = #{projectId}



    AND nu.ne_user_id LIKE #{neUserIdLike}



    AND nr.ne_repository_name LIKE #{neRepositoryNameLike}

SELECT nr.repository_id, nr.ne_repository_name, nu_o_def.user_id, nu_o_def.ne_user_id, nu_o_def.ne_user_password, nu_o_def.object_version_number FROM rdupm_nexus_repository nr INNER JOIN rdupm_nexus_user nu ON nu.repository_id = nr.repository_id INNER JOIN rdupm_nexus_user nu_o_def ON nr.repository_id = nu_o_def.repository_id AND nu_o_def.is_default = 1 WHERE 1 = 1
AND nr.organization_id = #{organizationId}
AND nr.project_id = #{projectId}
AND nu.ne_user_id = ( SELECT nu_def.ne_user_id FROM rdupm_nexus_repository nr_def INNER JOIN rdupm_nexus_user nu_def ON nr_def.repository_id = nu_def.repository_id WHERE nu_def.is_default = 1
AND nr_def.repository_id = #{repositoryId}
AND nr_def.organization_id = #{organizationId}
AND nr_def.project_id = #{projectId}
) ORDER BY nu.is_default DESC, nu.user_id
